import React, { useState } from 'react'; // Import useState
import { Link, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useCart } from '../context/CartContext';

const Navigation = () => {
  const { user, logout } = useAuth();
  const { getTotalItems } = useCart();
  const location = useLocation();
  const [showLogoutConfirm, setShowLogoutConfirm] = useState(false); // New state for confirmation dialog

  const getLinkClass = (path) => {
    const baseClass = "inline-flex items-center px-1 pt-1 border-b-2 text-sm font-medium";
    const activeClass = "border-primary text-gray-900";
    const inactiveClass = "border-transparent text-gray-500 hover:border-gray-300 hover:text-gray-700";

    return `${baseClass} ${location.pathname === path ? activeClass : inactiveClass}`;
  };

  const getCartLinkClass = (path) => {
    const baseClass = "text-gray-500 hover:text-gray-700 p-1 rounded-full";
    const activeClass = "text-primary";
    return `${baseClass} ${location.pathname === path ? activeClass : ''}`;
  };

  const getUserProfileLinkClass = (path) => {
    const baseClass = "text-gray-700";
    const activeClass = "font-bold text-primary";
    return `${baseClass} ${location.pathname.startsWith(path) ? activeClass : ''}`;
  };

  const handleLogout = () => {
    setShowLogoutConfirm(true); // Show confirmation dialog
  };

  const confirmLogout = () => {
    logout(); // Call logout function
    setShowLogoutConfirm(false); // Hide confirmation dialog
  };

  const cancelLogout = () => {
    setShowLogoutConfirm(false); // Hide confirmation dialog
  };

  return (
    <>
      <nav className="bg-white shadow-lg">
        <div className="max-w-7xl mx-auto px-4">
          <div className="flex justify-between h-16">
            <div className="flex">
              <Link to="/" className="flex-shrink-0 flex items-center">
                <span className="text-2xl font-bold text-primary">RE-loop</span>
              </Link>
              <div className="hidden sm:ml-6 sm:flex sm:space-x-8">
                <Link to="/" className={getLinkClass('/')}>
                  Home
                </Link>
                <Link to="/products" className={getLinkClass('/products')}>
                  Products
                </Link>
                <Link to="/categories" className={getLinkClass('/categories')}>
                  Categories
                </Link>
                {user && (
                  <Link to="/sell" className={`ml-6 ${getLinkClass('/sell')}`}>
                    Sell
                  </Link>
                )}
                {user && user.role === 'admin' && (
                  <Link to="/admin" className={getLinkClass('/admin')}>
                    Admin Panel
                  </Link>
                )}
              </div>
            </div>
            <div className="flex items-center">
              <Link to="/cart" className={getCartLinkClass('/cart')}>
                <i className="fas fa-shopping-cart text-xl"></i>
                {getTotalItems() > 0 && (
                  <span className="absolute top-2 right-2 inline-flex items-center justify-center px-2 py-1 text-xs font-bold leading-none text-white transform translate-x-1/2 -translate-y-1/2 bg-red-500 rounded-full">
                    {getTotalItems()}
                  </span>
                )}
              </Link>
              {user ? (
                <div className="ml-3 relative">
                  <div className="flex items-center space-x-3">
                    <Link to={`/user/${user.id}`} className={getUserProfileLinkClass(`/user/${user.id}`)}>
                      {user.name}
                    </Link>
                    <button
                      onClick={handleLogout} // Call handleLogout
                      className="text-gray-500 hover:text-gray-700"
                    >
                      Logout
                    </button>
                  </div>
                </div>
              ) : (
                <div className="flex items-center space-x-4">
                  <Link to="/login" className="text-gray-500 hover:text-gray-700">
                    Login
                  </Link>
                  <Link to="/register" className="bg-primary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-primary-dark">
                    Register
                  </Link>
                </div>
              )}
            </div>
          </div>
        </div>
      </nav>

      {/* Logout Confirmation Dialog */}
      {showLogoutConfirm && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center z-50">
          <div className="relative mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
            <div className="mt-3 text-center">
              <h3 className="text-lg leading-6 font-medium text-gray-900">Confirm Logout</h3>
              <div className="mt-2 px-7 py-3">
                <p className="text-sm text-gray-500">Are you sure you want to log out?</p>
              </div>
              <div className="items-center px-4 py-3">
                <button
                  onClick={confirmLogout}
                  className="px-4 py-2 bg-red-600 text-white text-base font-medium rounded-md w-full shadow-sm hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-red-500"
                >
                  Yes, Logout
                </button>
                <button
                  onClick={cancelLogout}
                  className="mt-3 w-full inline-flex justify-center rounded-md border border-gray-300 shadow-sm px-4 py-2 bg-white text-base font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary sm:mt-0 sm:ml-3 sm:w-auto sm:text-sm"
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default Navigation;
