import React from 'react';
import { useCart } from '../context/CartContext';
import { formatCurrency } from '../utils/currency';

const ProductCard = ({ product }) => {
  const { addToCart } = useCart();

  // Calculate discounted price
  const discountedPrice = product.discount 
    ? product.price * (1 - product.discount / 100)
    : product.price;

  return (
    <div className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition-shadow duration-300">
      <div className="h-48 bg-gray-200 flex items-center justify-center">
        {product.images && product.images.length > 0 ? (
          <img 
            src={product.images[0]} 
            alt={product.title} 
            className="h-full w-full object-cover"
          />
        ) : (
          <div className="text-4xl text-gray-400">
            <i className="fas fa-camera"></i>
          </div>
        )}
      </div>
      <div className="p-4">
        <div className="flex justify-between items-start">
          <h3 className="text-lg font-bold mb-1">{product.title}</h3>
          <div className="flex items-center">
            <span className="text-yellow-500 mr-1">
              <i className="fas fa-star"></i>
            </span>
            <span className="text-sm text-gray-600">{product.seller?.rating || 'N/A'}</span>
          </div>
        </div>
        <p className="text-gray-600 text-sm mb-3 line-clamp-2">{product.description}</p>
        <div className="flex justify-between items-center">
          <div>
            {product.discount > 0 ? (
              <div className="flex items-baseline">
                <span className="text-lg font-bold text-gray-900">{formatCurrency(discountedPrice)}</span>
                <span className="ml-2 text-sm text-gray-500 line-through">{formatCurrency(product.price)}</span>
                <span className="ml-2 text-sm font-bold text-green-600">{product.discount}% off</span>
              </div>
            ) : (
              <span className="text-lg font-bold text-gray-900">{formatCurrency(product.price)}</span>
            )}
          </div>
          <button 
            onClick={() => addToCart(product)}
            className="bg-indigo-600 hover:bg-indigo-700 text-white px-3 py-1 rounded text-sm"
          >
            Add to Cart
          </button>
        </div>
        <div className="mt-2 flex justify-between items-center">
          <span className="text-xs text-gray-500">By {product.seller?.name || 'Unknown Seller'}</span>
          <span className="text-xs text-gray-500">{product.stock} in stock</span>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
