import React from 'react';
import { Link, useLocation } from 'react-router-dom'; // Import useLocation
import { useAuth } from '../context/AuthContext';
import { useCart } from '../context/CartContext';

const Navigation = () => {
  const { user, logout } = useAuth();
  const { getTotalItems } = useCart();
  const location = useLocation(); // Get current location

  const getLinkClass = (path) => {
    const baseClass = "inline-flex items-center px-1 pt-1 border-b-2 text-sm font-medium";
    const activeClass = "border-primary text-gray-900";
    const inactiveClass = "border-transparent text-gray-500 hover:border-gray-300 hover:text-gray-700";

    return `${baseClass} ${location.pathname === path ? activeClass : inactiveClass}`;
  };

  const getCartLinkClass = (path) => {
    const baseClass = "text-gray-500 hover:text-gray-700 p-1 rounded-full";
    const activeClass = "text-primary"; // Highlight color for cart
    return `${baseClass} ${location.pathname === path ? activeClass : ''}`;
  };

  const getUserProfileLinkClass = (path) => {
    const baseClass = "text-gray-700";
    const activeClass = "font-bold text-primary"; // Highlight color for user name
    return `${baseClass} ${location.pathname.startsWith(path) ? activeClass : ''}`;
  };

  return (
    <>
      <nav className="bg-white shadow-lg">
        <div className="max-w-7xl mx-auto px-4">
          <div className="flex justify-between h-16">
            <div className="flex">
              <Link to="/" className="flex-shrink-0 flex items-center">
                <span className="text-2xl font-bold text-primary">RE-loop</span>
              </Link>
              <div className="hidden sm:ml-6 sm:flex sm:space-x-8">
                <Link to="/" className={getLinkClass('/')}>
                  Home
                </Link>
                <Link to="/products" className={getLinkClass('/products')}>
                  Products
                </Link>
                <Link to="/categories" className={getLinkClass('/categories')}>
                  Categories
                </Link>
                {user && ( // Show Sell button to all logged-in users
                  <Link to="/sell" className={`ml-6 ${getLinkClass('/sell')}`}>
                    Sell
                  </Link>
                )}
                {user && user.role === 'admin' && (
                  <Link to="/admin" className={getLinkClass('/admin')}>
                    Admin Panel
                  </Link>
                )}
              </div>
            </div>
            <div className="flex items-center">
              <Link to="/cart" className={getCartLinkClass('/cart')}>
                <i className="fas fa-shopping-cart text-xl"></i>
                {getTotalItems() > 0 && (
                  <span className="absolute top-2 right-2 inline-flex items-center justify-center px-2 py-1 text-xs font-bold leading-none text-white transform translate-x-1/2 -translate-y-1/2 bg-red-500 rounded-full">
                    {getTotalItems()}
                  </span>
                )}
              </Link>
              {user ? (
                <div className="ml-3 relative">
                  <div className="flex items-center space-x-3">
                    <Link to={`/user/${user.id}`} className={getUserProfileLinkClass(`/user/${user.id}`)}>
                      {user.name}
                    </Link>
                    <button
                      onClick={logout}
                      className="text-gray-500 hover:text-gray-700"
                    >
                      Logout
                    </button>
                  </div>
                </div>
              ) : (
                <div className="flex items-center space-x-4">
                  <Link to="/login" className="text-gray-500 hover:text-gray-700">
                    Login
                  </Link>
                  <Link to="/register" className="bg-primary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-primary-dark">
                    Register
                  </Link>
                </div>
              )}
            </div>
          </div>
        </div>
      </nav>
    </>
  );
};

export default Navigation;
