import React, { useState, useEffect } from 'react';
import { useCart } from '../context/CartContext';
import { formatCurrency } from '../utils/currency';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';
import BACKEND_URL from '../config';

const ProductCard = ({ product }) => {
  const { addToCart } = useCart();
  const { user } = useAuth();
  const navigate = useNavigate();
  const [isWishlisted, setIsWishlisted] = useState(false);

  useEffect(() => {
    const checkWishlistStatus = async () => {
      if (!user) return;
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get(`${BACKEND_URL}/api/wishlist`, {
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        });
        const wishlisted = response.data.wishlist.some(item => item.productId === product.id);
        setIsWishlisted(wishlisted);
      } catch (error) {
        console.error('Failed to fetch wishlist status:', error.response?.data?.error || error.message);
      }
    };
    checkWishlistStatus();
  }, [user, product.id]);

  const handleToggleWishlist = async (event) => {
    event.stopPropagation(); // Prevent card click
    if (!user) {
      navigate('/login');
      return;
    }

    try {
      const token = localStorage.getItem('token');
      if (isWishlisted) {
        await axios.delete(`${BACKEND_URL}/api/wishlist/${product.id}`, {
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        });
        setIsWishlisted(false);
      } else {
        await axios.post(`${BACKEND_URL}/api/wishlist`, { productId: product.id }, {
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        });
        setIsWishlisted(true);
      }
    } catch (error) {
      console.error('Failed to toggle wishlist:', error.response?.data?.error || error.message);
    }
  };

  const discountedPrice = product.discount 
    ? product.price * (1 - product.discount / 100)
    : product.price;

  return (
    <div className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-xl transition-shadow duration-300 flex flex-col h-[28rem] relative">
      <button
        onClick={handleToggleWishlist}
        className={`absolute top-2 right-2 z-10 p-2 rounded-full ${isWishlisted ? 'text-red-500 bg-red-100' : 'text-gray-400 bg-white'} hover:text-red-500 hover:bg-red-100 transition-colors duration-300`}
      >
        <i className="fas fa-heart text-xl"></i>
      </button>
      <Link to={`/products/${product.id}`} className="block flex flex-col h-full"> 
        <div className="h-64 bg-gray-200 flex items-center justify-center overflow-hidden">
          {product.images && product.images.length > 0 ? (
            <img 
              src={`${BACKEND_URL}${product.images[0].url}`}
              alt={product.title} 
              className="w-full h-full object-cover transform hover:scale-105 transition-transform duration-300"
            />
          ) : (
            <div className="text-4xl text-gray-400">
              <i className="fas fa-camera"></i>
            </div>
          )}
        </div>
        <div className="px-4 py-2 mt-auto"> 
          <h3 className="text-xl font-bold mb-2 text-base-text leading-tight line-clamp-2">{product.title}</h3>

          <div className="flex items-baseline mb-3">
            {product.discount > 0 ? (
              <>
                <span className="text-lg font-bold text-base-text">{formatCurrency(discountedPrice)}</span>
                <span className="ml-2 text-sm text-gray-500 line-through">{formatCurrency(product.price)}</span>
                <span className="ml-2 text-sm font-bold text-green-600">{product.discount}% off</span>
              </>
            ) : (
              <span className="text-lg font-bold text-base-text">{formatCurrency(product.price)}</span>
            )}
          </div>
        </div>
      </Link>
    </div>
  );
};

export default ProductCard;
