import React from 'react';
import { useCart } from '../context/CartContext';
import { formatCurrency } from '../utils/currency';
import { Link } from 'react-router-dom'; // Import Link
import BACKEND_URL from '../config';

const ProductCard = ({ product }) => {
  const { addToCart } = useCart();

  // Calculate discounted price
  const discountedPrice = product.discount 
    ? product.price * (1 - product.discount / 100)
    : product.price;

  return (
    <div className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-xl transition-shadow duration-300 flex flex-col">
      <Link to={`/products/${product.id}`} className="block"> {/* Make entire card clickable */}
        <div className="h-48 bg-gray-200 flex items-center justify-center overflow-hidden">
          {product.images && product.images.length > 0 ? (
            <img 
              src={`${BACKEND_URL}${product.images[0].url}`} // Prepend backend URL
              alt={product.title} 
              className="w-full h-full object-cover transform hover:scale-105 transition-transform duration-300"
            />
          ) : (
            <div className="text-4xl text-gray-400">
              <i className="fas fa-camera"></i>
            </div>
          )}
        </div>
        <div className="p-4 flex-grow">
          <h3 className="text-xl font-bold mb-2 text-base-text leading-tight">{product.title}</h3> {/* Larger title */}
          <div className="flex items-center mb-2">
            <span className="text-yellow-500 mr-1">
              <i className="fas fa-star"></i>
            </span>
            <span className="text-sm text-gray-600">{product.seller?.rating || 'N/A'}</span>
            <span className="mx-2 text-gray-300">â€¢</span>
            <span className="text-sm text-gray-600">By {product.seller?.name || 'Unknown Seller'}</span>
          </div>
          <p className="text-gray-700 text-sm mb-3 line-clamp-2">{product.description}</p>
          <div className="flex items-baseline mb-3">
            {product.discount > 0 ? (
              <>
                <span className="text-lg font-bold text-base-text">{formatCurrency(discountedPrice)}</span>
                <span className="ml-2 text-sm text-gray-500 line-through">{formatCurrency(product.price)}</span>
                <span className="ml-2 text-sm font-bold text-green-600">{product.discount}% off</span>
              </>
            ) : (
              <span className="text-lg font-bold text-base-text">{formatCurrency(product.price)}</span>
            )}
          </div>
        </div>
      </Link>
      <div className="p-4 border-t border-gray-200 flex justify-between items-center">
        <span className="text-xs text-gray-500">{product.stock} in stock</span>
        <button 
          onClick={() => addToCart(product)}
          className="bg-primary hover:bg-primary-dark text-white px-4 py-2 rounded-md text-sm font-medium transition duration-300 transform hover:scale-105"
        >
          Add to Cart
        </button>
      </div>
    </div>
  );
};

export default ProductCard;
