import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { useProducts } from '../context/ProductContext';
import { useCart } from '../context/CartContext';
import { formatCurrency } from '../utils/currency';

const ProductDetail = () => {
  const { id } = useParams();
  const { getProductById } = useProducts();
  const { addToCart } = useCart();
  const [selectedImage, setSelectedImage] = useState(0);
  const [quantity, setQuantity] = useState(1);

  const product = getProductById(id);

  if (!product) {
    return (
      <div className="max-w-7xl mx-auto px-4 py-8">
        <div className="text-center">
          <h2 className="text-2xl font-bold text-gray-900 mb-4">Product Not Found</h2>
          <p className="text-gray-600">The product you're looking for doesn't exist or has been removed.</p>
        </div>
      </div>
    );
  }

  // Calculate discounted price
  const discountedPrice = product.discount 
    ? product.price * (1 - product.discount / 100)
    : product.price;

  const handleAddToCart = () => {
    addToCart(product, quantity);
  };

  return (
    <div className="max-w-7xl mx-auto px-4 py-8">
      <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
        {/* Product Images */}
        <div>
          <div className="bg-gray-200 rounded-lg overflow-hidden mb-4">
            {product.images && product.images.length > 0 ? (
              <img 
                src={product.images[selectedImage]} 
                alt={product.title} 
                className="w-full h-96 object-contain"
              />
            ) : (
              <div className="w-full h-96 flex items-center justify-center text-gray-400">
                <i className="fas fa-camera text-6xl"></i>
              </div>
            )}
          </div>

          {product.images && product.images.length > 1 && (
            <div className="flex space-x-2">
              {product.images.map((image, index) => (
                <button
                  key={index}
                  onClick={() => setSelectedImage(index)}
                  className={`w-20 h-20 bg-gray-200 rounded-md overflow-hidden ${selectedImage === index ? 'ring-2 ring-indigo-500' : ''}`}
                >
                  <img 
                    src={image} 
                    alt={`Product ${index + 1}`} 
                    className="w-full h-full object-cover"
                  />
                </button>
              ))}
            </div>
          )}
        </div>

        {/* Product Info */}
        <div>
          <h1 className="text-3xl font-bold text-gray-900 mb-2">{product.title}</h1>
          <div className="flex items-center mb-4">
            <div className="flex items-center">
              {[...Array(5)].map((_, i) => (
                <span key={i} className={`text-lg ${i < Math.floor(product.seller?.rating || 0) ? 'text-yellow-400' : 'text-gray-300'}`}>
                  <i className="fas fa-star"></i>
                </span>
              ))}
              <span className="ml-2 text-gray-600">{product.seller?.rating || 'N/A'}</span>
            </div>
            <span className="mx-2 text-gray-300">â€¢</span>
            <span className="text-gray-600">By {product.seller?.name || 'Unknown Seller'}</span>
          </div>

          <div className="mb-6">
            {product.discount > 0 ? (
              <div className="flex items-baseline">
                <span className="text-3xl font-bold text-gray-900">{formatCurrency(discountedPrice)}</span>
                <span className="ml-3 text-xl text-gray-500 line-through">{formatCurrency(product.price)}</span>
                <span className="ml-3 text-lg font-bold text-green-600">{product.discount}% off</span>
              </div>
            ) : (
              <span className="text-3xl font-bold text-gray-900">{formatCurrency(product.price)}</span>
            )}
          </div>

          <p className="text-gray-700 mb-6">{product.description}</p>

          <div className="mb-6">
            <h3 className="text-lg font-medium text-gray-900 mb-2">Product Details</h3>
            <ul className="list-disc list-inside text-gray-600 space-y-1">
              <li>Category: {product.category}</li>
              <li>In Stock: {product.stock} items</li>
              <li>Tags: {product.tags.join(', ')}</li>
            </ul>
          </div>

          <div className="border-t border-b border-gray-200 py-6 mb-6">
            <div className="flex items-center">
              <span className="mr-4 text-gray-700">Quantity:</span>
              <div className="flex items-center">
                <button 
                  onClick={() => setQuantity(Math.max(1, quantity - 1))}
                  className="text-gray-500 hover:text-gray-700 w-8 h-8 flex items-center justify-center border border-gray-300 rounded-l-md"
                >
                  -
                </button>
                <span className="w-12 h-8 flex items-center justify-center border-t border-b border-gray-300">
                  {quantity}
                </span>
                <button 
                  onClick={() => setQuantity(Math.min(product.stock, quantity + 1))}
                  className="text-gray-500 hover:text-gray-700 w-8 h-8 flex items-center justify-center border border-gray-300 rounded-r-md"
                >
                  +
                </button>
              </div>
              <span className="ml-4 text-gray-600">{product.stock} items available</span>
            </div>
          </div>

          <div className="flex space-x-4">
            <button
              onClick={handleAddToCart}
              className="flex-1 bg-indigo-600 text-white py-3 rounded-md font-medium hover:bg-indigo-700"
            >
              Add to Cart
            </button>
            <button className="px-4 py-3 border border-gray-300 rounded-md text-gray-700 hover:bg-gray-50">
              <i className="fas fa-heart"></i>
            </button>
          </div>
        </div>
      </div>

      {/* Product Description */}
      <div className="mt-12">
        <h2 className="text-2xl font-bold text-gray-900 mb-4">Product Description</h2>
        <div className="bg-white rounded-lg shadow-md p-6">
          <p className="text-gray-700">{product.description}</p>
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
